mapper.select:
select

from Schedule s
left join StartURL u on s.startURLId = u.id


        u.siteId like CONCAT('%','${match}','%' )


        and u.startURL like CONCAT('%','${url}','%' )


        and u.name like CONCAT('%','${channelName}','%' )


        and u.jobname like CONCAT('%','${jobname}','%' )



        and s.locationId =#{locationId}



        and s.scheduleType=#{type}



    order by ${sort}


     limit #{start}, #{limit}

mapper.update:
update Schedule
startURLId = #{startURLId}, jobId = #{jobId}, schedule = #{schedule}, locationId = #{locationId}, scheduleType = #{scheduleType}, status=#{status}
where id = #{id}
