function getWeiboDaren() {
	var html = [], user = userData,
		userUrl = 'http://t.163.com/' + user.screenName + '?method=click&keyfrom=wenzhang_dr&sitechannel=news';
	html.push('<div class="daren-item">');
	html.push('<div class="avatar">');
	html.push('<a target="_blank" href="' + userUrl + '"><img src="' + user.headPic + '"></img></a>');
	html.push('<a href="javascript:;" target="_self" hideFocus="true" class="wb-article-btn wb-article-folow" onclick="WbApp.WbFollow(\'' + user.screenName + '\',this,{followfrom:\'wenzhang_DR\',keyfrom:\'wenzhang_DR\',sitechannel:\'news\'}); return false;">已关注</a>');
	html.push('</div><div class="author-info">');
	html.push('<a target="_blank" class="author-name" href="' +userUrl + '">' + user.name + '</a>');
	if(user.daren == true) {
		html.push('<a class="daren-icon" href="http://t.163.com/rank/daren" title="网易微博 i 认证"></a>');
	}
	html.push('<a class="talk-to" href="javascript:;" target="_self" onclick="WbApp.WbSay(\'' + user.name + '\',{keyfrom:\'wenzhang_DR\',sitechannel:\'news\'}); return false;">对Ta说</a><br />');
	html.push('<span class="followers">' + user.follower + '人关注他</span></div>');
	html.push('<div class="reason">' + user.reason + '</div></div>');
	return html.join('');
}
// 微博消息
function getWeiboMsg(data) {
	var html = [], user = data.user, 
		userUrl = 'http://t.163.com/' + user.screenName + '?method=click&keyfrom=wenzhang_mr&sitechannel=news',
		tweetUrl = 'http://t.163.com/' + user.screenName + '/status/' + data.tweetId + '?method=click&keyfrom=wenzhang_mr&sitechannel=news';
	html.push('<div class="msg-item"><div class="avatar">');
	html.push('<a target="_blank" href="' + userUrl + '"><img src="' + user.headPic + '"></img></a>');
	html.push('<a href="javascript:;" target="_self" hideFocus="true" class="wb-article-btn wb-article-folow" onclick="WbApp.WbFollow(\'' + user.screenName + '\',this,{followfrom:\'wenzhang_DR\',keyfrom:\'wenzhang_DR\',sitechannel:\'news\'}); return false;">已关注</a></div>');
	html.push('<div class="status-info"><div class="info-top">');
	html.push('<a target="_blank" class="author-name" href="' + userUrl + '">' + user.name + '</a>');
	if(user.daren == true) {
		html.push('<a class="daren-icon" href="http://t.163.com/rank/daren" title="网易微博 i 认证"></a>');
	}
	html.push('：<span class="status">' + data.tweet + '</span>');
	html.push('<span class="status-link">[<a target="_blank" href="' + tweetUrl + '">查看微博</a>]</span></div>');
	html.push('<div class="option-item"><a href="javascript:;" target="_self" onclick="WbApp.WbRetweet(\'' + data.tweetId + '\',{keyfrom:\'wenzhang_DR\',sitechannel:\'news\'}); return false;">转发</a> | ');
	html.push('<a href="javascript:;" target="_self" onclick="WbApp.WbComment(\'' + data.tweetId + '\',{keyfrom:\'wenzhang_DR\',sitechannel:\'news\'}); return false;">评论</a></div>');
	html.push('</div></div>');
	return html.join('');			
}
function renderDaren(data) {
	document.write(getWeiboDaren());
}
function renderMsg(data) {
	document.write(getWeiboMsg(data));
}
function getCoords(el) {
	var box = el.getBoundingClientRect(),
		doc = el.ownerDocument,
		body = doc.body,
		html = doc.documentElement,
		clientTop = html.clientTop || body.clientTop || 0,
		clientLeft = html.clientLeft || body.clientLeft || 0,
		top = box.top + (self.pageYOffset || html.scrollTop || body.scrollTop) - clientTop,
		left = box.left + (self.pageXOffset || html.scrollLeft || body.scrollLeft) - clientLeft;
	return { 'top': top, 'left': left };
};

