            var HouseCity = {

                url: "http://ip.ws.126.net/ipquery",

                check: function(isGd, isFs){
                    if (typeof isGd != "string" && typeof lo != "undefined" && typeof lc != "undefined") {
                        isGd = ("广东省" == lo && "深圳市" == lc) || lo != "广东省" ? 0 : 1;
                        NTES.cookie.set("isGd", isGd, 150 * 24 * 60, ".163.com","/");
                    }
                    if (1 == isGd) {
                        $("#houseUrl").href = "http://gz.house.163.com/";
                    }
                    if (typeof isFs != "string" && typeof lo != "undefined" && typeof lc != "undefined") {
                        isFs = ("广东省" == lo && "佛山市" == lc) || lo != "广东省" ? 0 : 1;
                        NTES.cookie.set("isFs", isFs, 150 * 24 * 60, ".163.com","/");
                    }
                    if (1 == isFs) {
                        $("#homeUrl").href = "http://gz.house.163.com/home/";
                    }
                },

                init: function(){
                    var isGd = NTES.cookie.get("isGd"), isFs = NTES.cookie.get("isFs");
                    if ("" == isGd) {
                        NTES.ajax.importJs(HouseCity.url, this.check);
                    }
                    else {
                        this.check(isGd, "");
                    }
                    if ("" == isFs) {
                        NTES.ajax.importJs(HouseCity.url, this.check);
                    }
                    else {
                        this.check("", isFs);
                    }
                }
            };
